	@Override
	public void onClick(View view) {
		// TODO Auto-generated method stub
		switch(view.getId()) {
		case R.id.iv_back:
			finish();
			break;
		case R.id.iv_save:
			modifyMainAttach();
			break;
		case R.id.iv_rotate_photo:
			rotateMultiple++;
			if (rotateMultiple >= 4) {
				rotateMultiple = 0;
			}
			BitmapDrawable mDrawable =  (BitmapDrawable) photoView.getDrawable();
			Bitmap image = mDrawable.getBitmap();
			Bitmap bmp = BitmapUtils.rotate(image, 270, true);
			saveBmp = bmp;
			Matrix matrix = photoView.getImageMatrix();
			photoView.setImageBitmap(bmp);
			photoView.setImageMatrix(matrix);
			doneBtn.setEnabled(true);
			
			originalBmp = BitmapUtils.rotate(originalBmp, 270, true);
			break;
		case R.id.iv_clip_photo:
			if (photoClipUtil.isClipped()) {
				saveBmp = photoClipUtil.getClipBmp();
				originalBmp = photoClipUtil.getClipBmp(originalBmp);
				photoClipUtil.resetPic(saveBmp);
				isPhotoClipped = true;
				doneBtn.setEnabled(true);
				CmmobiClickAgent.onEvent(this, "cut_pic");
			}
			break;
//		case R.id.:
//			finishDialog.dismiss();
//			modifyMainAttach();
//			break;
//		case R.id.save_as:
//			finishDialog.dismiss();
//			break;
//		case R.id.cancel:
//			finishDialog.dismiss();
//			break;
		}
	}
	
